import React, { useState, useMemo } from 'react';
import { cheatsheets, categories, getFeaturedCheatsheets, getCheatsheetsByCategory, searchCheatsheets } from '../data/cheatsheets';
import CheatSheetCard from './CheatSheetCard';
import SearchBar from './SearchBar';
import CategoryFilter from './CategoryFilter';
import Hero from './Hero';
import Stats from './Stats';
import './LandingPage.css';

const LandingPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [showFeaturedOnly, setShowFeaturedOnly] = useState(false);

  const filteredCheatsheets = useMemo(() => {
    let filtered = cheatsheets;

    // Apply search filter
    if (searchQuery.trim()) {
      filtered = searchCheatsheets(searchQuery);
    }

    // Apply category filter
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(sheet => sheet.category === selectedCategory);
    }

    // Apply featured filter
    if (showFeaturedOnly) {
      filtered = filtered.filter(sheet => sheet.featured);
    }

    return filtered;
  }, [searchQuery, selectedCategory, showFeaturedOnly]);

  const featuredCheatsheets = getFeaturedCheatsheets();

  const handleSearch = (query) => {
    setSearchQuery(query);
    setShowFeaturedOnly(false); // Reset featured filter when searching
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setShowFeaturedOnly(false); // Reset featured filter when changing category
  };

  const toggleFeatured = () => {
    setShowFeaturedOnly(!showFeaturedOnly);
    setSearchQuery(''); // Clear search when toggling featured
    setSelectedCategory('All'); // Reset category when toggling featured
  };

  return (
    <div className="landing-page">
      <Hero />

      <Stats totalCheatsheets={cheatsheets.length} categories={categories.length - 1} />

      <main className="main-content">
        <div className="container">
          {/* Featured Section */}
          <section className="featured-section">
            <div className="section-header">
              <h2>🌟 Featured Cheat Sheets</h2>
              <p>Most popular and comprehensive references</p>
            </div>
            <div className="featured-grid">
              {featuredCheatsheets.map(sheet => (
                <CheatSheetCard key={sheet.id} cheatsheet={sheet} featured />
              ))}
            </div>
          </section>

          {/* Search and Filter Controls */}
          <section className="controls-section">
            <div className="controls-container">
              <SearchBar 
                onSearch={handleSearch}
                searchQuery={searchQuery}
                placeholder="Search cheat sheets..."
              />
              <CategoryFilter
                categories={categories}
                selectedCategory={selectedCategory}
                onCategoryChange={handleCategoryChange}
              />
              <button 
                className={`featured-toggle ${showFeaturedOnly ? 'active' : ''}`}
                onClick={toggleFeatured}
              >
                ⭐ Featured Only
              </button>
            </div>
          </section>

          {/* Results Section */}
          <section className="results-section">
            <div className="section-header">
              <h2>
                {showFeaturedOnly ? '⭐ Featured' : 
                 selectedCategory !== 'All' ? `📁 ${selectedCategory}` :
                 searchQuery ? `🔍 Search Results` : '📚 All Cheat Sheets'}
              </h2>
              <p>
                {filteredCheatsheets.length} cheat sheet{filteredCheatsheets.length !== 1 ? 's' : ''} found
                {searchQuery && ` for "${searchQuery}"`}
              </p>
            </div>

            {filteredCheatsheets.length > 0 ? (
              <div className="cheatsheets-grid">
                {filteredCheatsheets.map(sheet => (
                  <CheatSheetCard key={sheet.id} cheatsheet={sheet} />
                ))}
              </div>
            ) : (
              <div className="no-results">
                <div className="no-results-icon">🔍</div>
                <h3>No cheat sheets found</h3>
                <p>
                  {searchQuery ? 
                    `No results for "${searchQuery}". Try different keywords or browse by category.` :
                    'No cheat sheets match your current filters. Try adjusting your selection.'
                  }
                </p>
                <button 
                  className="reset-filters"
                  onClick={() => {
                    setSearchQuery('');
                    setSelectedCategory('All');
                    setShowFeaturedOnly(false);
                  }}
                >
                  Reset Filters
                </button>
              </div>
            )}
          </section>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>🚀 KnightyApp</h3>
              <p>Comprehensive developer cheat sheets for modern workflows</p>
            </div>
            <div className="footer-section">
              <h4>Categories</h4>
              <ul>
                {categories.slice(1, 6).map(category => (
                  <li key={category}>
                    <button onClick={() => handleCategoryChange(category)}>
                      {category}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
            <div className="footer-section">
              <h4>Popular</h4>
              <ul>
                {featuredCheatsheets.slice(0, 5).map(sheet => (
                  <li key={sheet.id}>
                    <a href={`/htmls/${sheet.file}`} target="_blank" rel="noopener noreferrer">
                      {sheet.icon} {sheet.title}
                    </a>
                  </li>
                ))}
              </ul>
            </div>
            <div className="footer-section">
              <h4>About</h4>
              <p>Built with ❤️ for developers who need quick, reliable command references.</p>
              <div className="footer-stats">
                <span>{cheatsheets.length} Cheat Sheets</span>
                <span>{categories.length - 1} Categories</span>
                <span>Always Updated</span>
              </div>
            </div>
          </div>
          <div className="footer-bottom">
            <p>&copy; 2024 KnightyApp. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default LandingPage;
