import React, { useState, useEffect, useRef, forwardRef, useImperativeHandle } from 'react';
import { useNavigate } from 'react-router-dom';
import { searchCheatsheets } from '../data/cheatsheets';
import './SearchBar.css';

const SearchBar = forwardRef(({ onSearch, searchQuery, placeholder = "Search..." }, ref) => {
  const [localQuery, setLocalQuery] = useState(searchQuery || '');
  const [searchResults, setSearchResults] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const inputRef = useRef(null);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  useImperativeHandle(ref, () => ({
    focus: () => {
      if (inputRef.current) {
        inputRef.current.focus();
      }
    },
    clear: () => {
      setLocalQuery('');
      setSearchResults([]);
      setShowDropdown(false);
      setSelectedIndex(-1);
      onSearch('');
    }
  }));

  useEffect(() => {
    setLocalQuery(searchQuery || '');
  }, [searchQuery]);

  // Handle search and update dropdown
  useEffect(() => {
    if (localQuery.trim()) {
      const results = searchCheatsheets(localQuery).slice(0, 8); // Limit to 8 results
      setSearchResults(results);
      setShowDropdown(results.length > 0);
      setSelectedIndex(-1);
    } else {
      setSearchResults([]);
      setShowDropdown(false);
      setSelectedIndex(-1);
    }
  }, [localQuery]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
        setSelectedIndex(-1);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setLocalQuery(value);
    onSearch(value);
  };

  const handleClear = () => {
    setLocalQuery('');
    onSearch('');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(localQuery);
  };

  return (
    <form className="search-bar" onSubmit={handleSubmit}>
      <div className="search-input-container">
        <div className="search-icon">
          üîç
        </div>
        <input
          ref={inputRef}
          type="text"
          className="search-input"
          placeholder={placeholder}
          value={localQuery}
          onChange={handleInputChange}
          autoComplete="off"
          spellCheck="false"
        />
        {localQuery && (
          <button
            type="button"
            className="clear-button"
            onClick={handleClear}
            aria-label="Clear search"
          >
            ‚úï
          </button>
        )}
      </div>
    </form>
  );
});

export default SearchBar;
